INSERT INTO `tbl_comments` (`comments_id`, `blog_id`, `user_id`, `comments`, `publication_status`, `created_date_time`) VALUES
(1, 3, 1, 'অ্যাডিলেডে ইংল্যান্ডকে হারানোর পর ভারতীয় কিংবদন্তি বলেছেন, ‘কোয়ার্টার ফাইনালে ইংল্যান্ডই ভারতের জন্য সহজ প্রতিপক্ষ হতো।’ ইতিহাস, ঐতিহ্য, ঐশ্বর্য থাকলেও ক্রিকেট-ফুটবলে ইংলিশদের অর্জন সামান্যই। গাভাস্কার তাই ইংল্যান্ডকে দিলেন একটা মোক্ষম খোঁচা, ‘ক্রিকেট, ফুটবল দুটোতেই ইংল্যান্ড একটা ‘‘ওভাররেটেড’’ দল।’ ', 0, '2015-03-16 06:10:21'),
(2, 1, 1, 'ইউপিএস: স্পার্ক পাওয়ার ৬৫০ভিএ ২,৮০০; অ্যাপোলো ৬৫০ভিএ ২৮৫০ ও ১২০০ভিএ ৫,০০০ টাকা। ', 1, '2015-03-16 08:56:39'),
(3, 1, 2, 'দীর্ঘদিন ধরে অবরোধ-হরতালের কারণে প্রযুক্তি বাজারের স্বাভাবিক বেচাকেনা নেই বললেই চলে। বিক্রেতারা জানান, শুক্র ও শনিবার ক্রেতাদের উপস্থিতি থাকলেও হরতাল থাকলে ক্রেতারা তেমন বাজারমুখী হন না। গতকাল সোমবার একাধিক বাজার ঘুরে পাওয়া যন্ত্রাংশের দাম নিচে দেওয়া হলো।', 1, '2015-03-16 09:08:53');

