  `email_address` varchar(100) NOT NULL,
  `password` varchar(32) NOT NULL,
  `age` int(3) NOT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;

